<?php/** * @package     Joomla.Administrator * @subpackage  COM_PRAYER * * @copyright   Copyright (C) 2005 - 2013 Open Source Matters, Inc. All rights reserved. * @license     GNU General Public License version 2 or later; see LICENSE.txt */defined('_JEXEC') or die;/** * Weblinks helper. * * @package     Joomla.Administrator * @subpackage  COM_PRAYER * @since       1.6 */class PrayerHelper extends JHelperContent{	public static $extension = 'com_prayer';	/**	 * Configure the Linkbar.	 *	 * @param   string  $vName  The name of the active view.	 *	 * @return void	 *	 * @since   1.6	 */	public static function addSubmenu($vName)	{		JHtmlSidebar::addEntry(			JText::_('COM_PRAYER_SUBMENU_CPANEL'),			'index.php?option=com_prayer',			$vName == 'prayer'		);		JHtmlSidebar::addEntry(			JText::_('COM_PRAYER_SUBMENU_REQUESTS'),			'index.php?option=com_prayer&view=reqs',			$vName == 'reqs'		);		JHtmlSidebar::addEntry(			JText::_('COM_PRAYER_SUBMENU_SUBSCRIBES'),			'index.php?option=com_prayer&view=subs',			$vName == 'subs'		);		JHtmlSidebar::addEntry(			JText::_('COM_PRAYER_SUBMENU_CSS'),			'index.php?option=com_prayer&view=css',			$vName == 'css'		);		JHtmlSidebar::addEntry(			JText::_('COM_PRAYER_SUBMENU_FILES'),			'index.php?option=com_prayer&view=files',			$vName == 'files'		);		JHtmlSidebar::addEntry(			JText::_('COM_PRAYER_SUBMENU_LANGUAGE_FILES'),			'index.php?option=com_prayer&view=langs',			$vName == 'langs'		);		JHtmlSidebar::addEntry(			JText::_('COM_PRAYER_SUBMENU_DEVOTIONALS'),			'index.php?option=com_prayer&view=devotions',			$vName == 'devotions'		);		JHtmlSidebar::addEntry(			JText::_('COM_PRAYER_SUBMENU_LINKS'),			'index.php?option=com_prayer&view=links',			$vName == 'links'		);		JHtmlSidebar::addEntry(			JText::_('COM_PRAYER_SUBMENU_CATEGORIES'),			'index.php?option=com_categories&extension=com_prayer',			$vName == 'categories'		);		if ($vName == 'categories')		{			JToolbarHelper::title(				JText::sprintf('COM_CATEGORIES_CATEGORIES_TITLE', JText::_('com_prayer')),				'prayer-categories');		}	}	/**	 * Gets a list of the actions that can be performed.	 *	 * @param   string   $component  The component name.	 * @param   string   $section    The access section name.	 * @param   integer  $id         The item ID.	 *	 * @return  JObject	 *	 * @since   3.2	 */	public static function getActions($component = '', $section = '', $id = 0)	{		// Check for deprecated arguments order		if (is_int($component) || is_null($component))		{			$result = JHelperContent::getActions($component, $section, $id);			return $result;		}		$assetName = $component;		if ($section && $id)		{			$assetName .=  '.' . $section . '.' . (int) $id;		}		$result = new JObject;		$user = JFactory::getUser();		$actions = JAccess::getActionsFromFile(			JPATH_ADMINISTRATOR . '/components/' . $component . '/access.xml', '/access/section[@name="component"]/'		);		if ($actions === false)		{			JLog::add(				JText::sprintf('JLIB_ERROR_COMPONENTS_ACL_CONFIGURATION_FILE_MISSING_OR_IMPROPERLY_STRUCTURED', $component), JLog::ERROR, 'jerror'			);			return $result;		}		foreach ($actions as $action)		{			$result->set($action->name, $user->authorise($action->name, $assetName));		}		return $result;	}}