<?php/** * prayer Component * * @license http://www.gnu.org/copyleft/gpl.html GNU/GPL * */// no direct accessdefined('_JEXEC') or die('Restricted access');/** * @package  ${NAMESPACE} * * @since  4.0 */class CWMPrayerVersion{	/** @var string Product */	public $PRODUCT = 'CWMPrayer';	/** @var int Main Release Level */	public $RELEASE = '3';	/** @var int Sub Release Level */	public $DEV_LEVEL = '0';	/** @var string Patch Level */	public $PATCH_LEVEL = '1';	/** @var string Development Status */	public $DEV_STATUS = '';	/** @var string Copyright Text */	public $COPYRIGHT = 'Joomla Bible Study Team &copy; 2017-';	/** @var string Copyright Text */	public $COPYRIGHTBY = 'Joomla Bible Study Team';	/** @var string LINK */	public $LINK = 'http://www.joomlabiblestiudy.com';	/**	 * ??	 *	 * @return CWMPrayerVersion	 *	 * @since 4.0	 */	public static function &getInstance()	{		static $instance;		if ($instance == null)		{			$instance = new CWMPrayerVersion;		}		return $instance;	}	/**	 * ?	 *	 * @param   string  $property  Name	 *	 * @return null	 *	 * @since 4.0	 */	public function get($property)	{		if (isset($this->$property))		{			return $this->$property;		}		return null;	}	/**	 * Return the URL	 *	 * @return string URL	 *	 * @since 4.0	 */	public function getUrl()	{		return $this->LINK;	}	/**	 * Return Copyright	 *	 * @return string short Copyright	 *	 * @since 4.0	 */	public function getShortCopyright()	{		return $this->COPYRIGHT . date('Y');	}	/**	 * Long Copyright	 *	 * @return string long Copyright	 *	 * @since 4.0	 */	public function getLongCopyright()	{		$copyright = $this->COPYRIGHT . date('Y');		return $copyright . ' ' . $this->COPYRIGHTBY;	}	/**	 * Long Version	 *	 * @return string Long format version	 *	 * @since 4.0	 */	public function getLongVersion()	{		return ' v.' . $this->getShortVersion();	}	/**	 * Short Version	 *	 * @return string Short version format	 *	 * @since 4.0	 */	public function getShortVersion()	{		return $this->RELEASE . '.' . $this->DEV_LEVEL . '.' . $this->PATCH_LEVEL . ' ' . $this->DEV_STATUS;	}	/**	 * Return version form upgrade server	 *	 * @return SimpleXMLElement|stdClass	 *	 * @since 4.0	 */	public function getCWMPrayerVersion()	{		$url         = "http://www.joomlabiblestudy.org/index.php?option=com_ars&view=update&task=stream&format=xml&id=10&dummy=extension.xml";		$data        = @file_get_contents($url);		if ($data)		{			$xmlObj  = simplexml_load_string($data);			$xmlObj->upglink = $xmlObj->update->downloads->downloadurl;		}		else		{			$xmlObj = new stdClass;			$xmlObj->update = new stdClass;			$xmlObj->update->version = '';			$xmlObj->upglink = '';		}		return $xmlObj;	}}