<?php/** * Prayer Component * * @license http://www.gnu.org/copyleft/gpl.html GNU/GPL * */use Joomla\Registry\Registry;defined('_JEXEC') or die('Restricted Access');/** * Provides access to the #__cwmprayer table */class TablePrayer extends JTable{	/** @var int Unique id */	public $id = null;	/** @var int */	public $checked_out = null;	/** @var datetime */	public $checked_out_time = null;	/**	 * Contructer	 *	 * @param   JDatabaseDriver  &$db  A database connector object	 *	 * @since 4.0	 */	public function __construct(&$db)	{		parent::__construct('#__cwmprayer', 'id', $db);	}	/**	 * @param array|object $array	 * @param string       $ignore	 *	 * @return bool	 *	 * @since version	 */	public function bind($array, $ignore = '')	{		if (isset($array['params']) && is_array($array['params']))		{			$registry = new Registry;			$registry->loadArray($array['params']);			$array['params'] = (string) $registry;		}		return parent::bind($array, $ignore);	}	/**	 * Method to perform sanity checks on the JTable instance properties to ensure they are safe to store in the database.	 *	 * Child classes should override this method to make sure the data they are storing in the database is safe and as expected before storage.	 *	 * @return  boolean  True if the instance is sane and able to be stored in the database.	 *	 * @since   11.1	 */	public function check()	{		if (empty($this->created))		{			$this->created = date('Y-m-d H:i:s');		}		return true;	}}