<?php// No direct accessdefined('_JEXEC') or die('Restricted access');class CWMPrayerViewLang extends JViewLegacy{	public $edit;	public $editor;	protected $file;	/**	 * Execute and display a template script.	 *	 * @param   string  $tpl  The name of the template file to parse; automatically searches through the template paths.	 *	 * @return  mixed  A string if successful, otherwise an Error object.	 *	 * @see     JViewLegacy::loadTemplate()	 * @since   3.0	 */	public function display($tpl = null)	{		$editor = JEditor::getInstance('none');		$input = JFactory::getApplication()->input;		$edit  = $input->get('edit', true);		$files = $input->getArray('cid');		if ($edit)		{			$file = $files[0];		}		else		{			$file = "";		}		$this->edit = $edit;		$this->editor = $editor;		$this->file = $file;		$this->addToolbar();		return parent::display($tpl);	}	/**	 * Add Tool Bar	 *	 * @return void	 *	 * @since  4.0.0	 */	protected function addToolbar()	{		JFactory::getApplication()->input->set('hidemainmenu', true);		JToolBarHelper::title(JText::_('COM_CWMPRAYER_TITLE_LANG'));		JToolBarHelper::help('language.help.html', true);		JToolBarHelper::save('prayer.savelang', 'Save');		if (!$this->edit)		{			JToolBarHelper::cancel('prayer.canceleditlang');		}		else		{			JToolBarHelper::cancel('prayer.canceleditlang', 'Close');		}	}}