<?phpdefined('_JEXEC') or die('Restricted access');class PrayerViewManageFiles extends JViewLegacy{	protected $items;	protected $pagination;	protected $state;	public function display($tpl = null)	{		$this->items = $this->get('Items');		$this->state = $this->get('State');		$this->pagination = $this->get('Pagination');		// Check for errors.		if (count($errors = $this->get('Errors')))		{			JError::raiseError(500, implode("\n", $errors));			return false;		}		PrayerHelper::addSubmenu('managefiles');		$this->addToolbar();		$this->sidebar = JHtmlSidebar::render();		parent::display($tpl);	}	protected function addToolbar()	{		JToolBarHelper::title('prayer - Manage Files');		JHtmlSidebar::setAction('index.php?option=com_prayer');	}}