<?php/** * prayer Component * * @license http://www.gnu.org/copyleft/gpl.html GNU/GPL * */defined('_JEXEC') or die();class PrayerViewManageReq extends JViewLegacy{	protected $items;	protected $pagination;	protected $state;	/**	 * Execute and display a template script.	 *	 * @param   string  $tpl  The name of the template file to parse; automatically searches through the template paths.	 *	 * @return  mixed  A string if successful, otherwise an Error object.	 *	 * @see     JViewLegacy::loadTemplate()	 * @since   3.0	 */	public function display($tpl = null)	{		$this->items      = $this->get('Items');		$this->state      = $this->get('State');		$this->pagination = $this->get('Pagination');		// Check for errors.		if (count($errors = $this->get('Errors')))		{			JFactory::getApplication()->enqueueMessage(implode("\n", $errors), 'error');			return false;		}		PrayerHelper::addSubmenu('managereq');		$this->addToolbar();		$this->sidebar = JHtmlSidebar::render();		return parent::display($tpl);	}	/**	 * Add the page title and toolbar.	 *	 * @since    4.0	 * @return void	 */	protected function addToolbar()	{		JToolbarHelper::title(JText::_('prayer - Manage Requests'));		JToolBarHelper::publishList("prayer.publish", 'Publish');		JToolBarHelper::unpublishList("prayer.unpublish", 'Unpublish');		JToolBarHelper::archiveList("prayer.archive", 'Archive');		JToolBarHelper::unarchiveList("prayer.unarchive", 'Unarchive');		JToolBarHelper::editList("edit", 'Edit');		JToolBarHelper::deleteList("Remove Request(s)?", "prayer.remove_req", 'Remove');		$cb = JToolBar::getInstance('toolbar');		$cb->appendButton(			'confirm',			JText::_('COM_PRAYER_CUSTOM_BUTTON'),			'apply',			'Purge',			'prayer.purge',			false		);		JHtmlSidebar::setAction('index.php?option=com_prayer');	}	/**	 * Returns an array of fields the table can be sorted by	 *	 * @return  array  Array containing the field name to sort by as the key and display text as value	 *	 * @since   3.0	 */	protected function getSortFields()	{		return [			'a.ordering'     => JText::_('JGRID_HEADING_ORDERING'),			'a.requester'    => JText::_('Requester'),			'a.request'      => JText::_('Request'),			'a.publishstate' => JText::_('JPUBLISHED'),			'a.archivestate' => JText::_('Archive State'),			'a.displaystate' => JText::_('Display State'),			'a.id'           => JText::_('JGRID_HEADING_ID')		];	}}