<?php/** * prayer Component * * @license http://www.gnu.org/copyleft/gpl.html GNU/GPL * */// No direct accessdefined('_JEXEC') or die('Restricted access');class CWMPrayerViewReq extends JViewLegacy{	protected $state;	protected $item;	protected $form;	public function display($tpl = null)	{		$this->state = $this->get('State');		$this->item = $this->get('Item');		$this->form = $this->get('Form');		$this->edit = $this->get('edit');		// Check for errors.		if (count($errors = $this->get('Errors')))		{			JError::raiseError(500, implode("\n", $errors));			return false;		}		$lists = array();		$plan = JFactory::getApplication()->input->getBool('plan', true);		if (!isset($plan))		{			$plan = "";		}		$this->lists = $lists;		$this->plan = $plan;		$this->addToolbar();		return parent::display($tpl);	}	protected function addToolbar()	{		JFactory::getApplication()->input->set('hidemainmenu', true);		$isNew = ($this->item->id == 0);		JToolBarHelper::title('Prayer - Edit Request');		JToolbarHelper::apply('req.apply');		JToolbarHelper::save('req.save');		if (!$isNew)		{			JToolBarHelper::cancel('req.cancel');		}		else		{			JToolBarHelper::cancel('req.cancel', 'Close');		}	}}