<?phpdefined('_JEXEC') or die('Restricted access');class CWMPrayerViewShowReq extends JViewLegacy{	/**	 * Execute and display a template script.	 *	 * @param   string  $tpl  The name of the template file to parse; automatically searches through the template paths.	 *	 * @return  mixed  A string if successful, otherwise an Error object.	 *	 * @see     JViewLegacy::loadTemplate()	 * @since   3.0	 */	public function display($tpl = null)	{		$this->version = new JVersion;		$eid = JRequest::getVar('id', null, 'get', 'int');		$db = JFactory::getDBO();		$db->setQuery("SELECT * FROM #__cwmprayer WHERE id='" . $eid . "'");		$this->erow = $db->loadObjectList();		return parent::display($tpl);	}}